package com.github.service;import com.github.model.User;import org.springframework.amqp.rabbit.core.RabbitTemplate;import org.springframework.scheduling.annotation.Scheduled;import org.springframework.stereotype.Service;import javax.annotation.Resource;@Servicepublic class ProducerService {	private Integer count = 0;	@Resource private RabbitTemplate rabbitTemplate;	@Scheduled(fixedDelay = 6 * 1000)	public void produce() {		User user = new User(10, "中国China123: " + count++);		rabbitTemplate.convertAndSend("hello", user);	}}