package com.github.service;import com.github.model.User;import org.springframework.data.redis.core.RedisTemplate;import org.springframework.scheduling.annotation.Scheduled;import org.springframework.stereotype.Service;import javax.annotation.Resource;@Servicepublic class PublisherService {	private Integer count = 0;	@Resource private RedisTemplate redisTemplate;	public static final String channel = "spring-boot-redis-pubsub";	@Scheduled(fixedDelay = 6 * 1000)	public void produce() {		User user = new User(10, "中国China123: " + count++);		redisTemplate.convertAndSend(channel, user);	}}